let modal_progress;function setBarWidth(e){let t=Math.round(100*e)/100;t>100&&(t=100);const o=document.getElementById("myBar");o.style.width=t+"%",o.innerHTML=t+"%"}function showProgressBar(){modal_progress||(modal_progress=new bootstrap.Modal("#progress-modal",{keyboard:!1})),modal_progress.show()}function hideProgressBar(){modal_progress&&modal_progress.hide()}function setProgressTitle(e){document.getElementById("progress-title").textContent=e}function errorMessageBox(e,t){document.getElementById("alert-modal-title").textContent=e,document.getElementById("alert-modal-text").textContent=t;new bootstrap.Modal(document.getElementById("alert-modal")).show()}